import React from 'react';
import LazyHero from 'react-lazy-hero';
import CookieConsent from "react-cookie-consent";
import {Helmet} from "react-helmet";
import AnchorLink from 'react-anchor-link-smooth-scroll';
import ScrollableAnchor from 'react-scrollable-anchor';

import "react-responsive-carousel/lib/styles/carousel.min.css";

import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';
import Weppes from'./Pics/Hero.png';


export default class Home extends React.Component {

  componentWillUnmount(){
    window.scrollTo(0, 0)
  }

	render(){
		return(
			<div>

				{/* Hero-Image */}
        <ScrollableAnchor id={'hero'}>
				    <LazyHero color = "#000000" parallaxOffset="50" minHeight='100vh' opacity ="0.5" imageSrc={Weppes}/>
        </ScrollableAnchor>

        <ScrollableAnchor id={'team'}>
        <div class="jumbotron jumbotron-fluid">
         <div class="container">
           <h2 class="display-4">Fluid jumbotron</h2>
           <p class="lead">This is a modified jumbotron that occupies the entire horizontal space of its parent.</p>
         </div>
        </div>
        </ScrollableAnchor>

				{/*	Hier wird der Cookie und deren Parameter bestimmt	*/}
				<CookieConsent
		    location="bottom"
        buttonText=<a href="/contact" >Weitere Informationen.</a>
		    cookieName="CookieConsent"
        acceptOnScroll={true}
        acceptOnScrollPercentage={10}
		    style={{ background: "#2B373B" }}
		    buttonStyle={{ color: "#4e503b", fontSize: "13px" }}
		    expires={150}>

		    Diese Webseite benutzt Cookies, um die Benutzererfahrung zu verbessern. Indem Sie diese Website nutzen, erkl√§ren Sie sich mit dieser Verwendung einverstanden.
				</CookieConsent>
			</div>
		);
	}
}
