import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Animation, MDBJumbotron, MDBContainer,MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardTitle, MDBCardText } from "mdbreact";
import '../Home.css';
import { connect } from 'react-redux';

var QRCode = require('qrcode.react');

class QR extends React.Component {
	constructor(props) {
    super(props);
  }

	componentDidMount(){
		this.props.dispatch({ type:"NAVCHANGE"});

	}

  componentWillUnmount(){
    window.scrollTo(0, 0)
  }

	render(){
		return(
			<div className="Home">
			<LazyHero color = "#000000" parallaxOffset="50" minHeight='100vh' opacity ="0.5" imageSrc={Hero} >
			 <MDBContainer className= "grid">
				 {/* 	Hier die trasnparente Box mit allem	*/}
				 <div className= "HeroText">
					 <MDBRow>
						 <MDBCol> <img src={Brand} width="25%" height="100%" alt="ProfilePicture"rounded responsive /></MDBCol>
					 </MDBRow>
					 <MDBRow>
						 <MDBCol><h1> Digitalisiere dein Event!</h1></MDBCol>
					 </MDBRow>
					 <MDBRow>
						 <MDBCol><h3> Seite im Aufbau...</h3></MDBCol>
					 </MDBRow>
				 </div>
			 </MDBContainer>
		 </LazyHero>
			</div>
		);
	}
}

export default connect()(QR);
