import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';
var QRCode = require('qrcode.react');


export default class QR extends React.Component {
	constructor(props) {
    super(props);
    this.state = { random: 0 };
  }

	componentDidMount(){
		const min = 1;
    const max = 100;
    const rand = min + Math.random() * (max - min);
    this.setState({ random: this.state.random + rand });
	}

  componentWillUnmount(){
    window.scrollTo(0, 0)
  }

	render(){


		return(
			<div className="Home" style={{"padding":"10%", "text-align":"center", "width":"100%", "height":"100%"}}>
				<QRCode includeMargin="true" value={this.state.random} />
			</div>
		);
	}
}
