import React from "react";
import LazyHero from "react-lazy-hero";
import CookieConsent from "react-cookie-consent";
import { Helmet } from "react-helmet";
import { Container, Row, Col } from "reactstrap";
import {
  Animation,
  MDBBtn,
  MDBJumbotron,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInput,
  MDBIcon,
  MDBCarousel,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBMask,
  MDBTable,
  MDBTableBody
} from "mdbreact";
import { connect } from "react-redux";

import S1 from "./Pics/symbols/1.png";
import S2 from "./Pics/symbols/2.png";
import S3 from "./Pics/symbols/3.png";
import S4 from "./Pics/symbols/4.png";

import "bootstrap/dist/css/bootstrap.min.css";
import "./Home.css";

class Pakethub extends React.Component {
  // Scroll to top when changing sites

  componentWillUnmount() {
    window.scrollTo(0, 0);
  }

  componentDidMount() {
    this.props.dispatch({ type: "NAVHOME" });
  }

  render() {
    return (
      <div className="Home Background">
        {/* Hero-Image newes Version*/}
        <LazyHero
          color="#ffffff"
          minHeight="50vh"
          style={{ background: "#ffffff" }}
        >
          <MDBContainer className="Jumbotron3" fluid no-gutters>
            <MDBRow no-gutters>
              <MDBCol size="12" style={{ minHeight: "10vh" }}>
                <h1
                  style={{
                    padding: "1%",
                    fontSize: "2em"
                  }}
                >
                  <b>
                    {" "}
                    Profitiere durch PAXET durch die Paketannahme für deine
                    Nachbarn mit nur einer Registrierung -
                    <br />
                    als PAXET-Pakethub!
                  </b>
                </h1>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
        </LazyHero>

        <MDBContainer className="Jumbotron3" fluid no-gutters>
          <MDBRow no-gutters>
            <MDBCol size="12">
              <h1
                style={{
                  "padding-bottom": "1%",
                  "padding-top": "1%",
                  "padding-left": "4%",
                  "padding-right": "4%",
                  fontSize: "3em"
                }}
              >
                <br />
                In wenigen Schritten zum PAXET-Pakethub
              </h1>
            </MDBCol>
          </MDBRow>
          <MDBRow
            no-gutters
            style={{ "padding-left": "20%", "padding-right": "20%" }}
          >
            <MDBCol lg="1" md="12" sm="12" className="align-self-right">
              <img src={S1} className="img-fluid" alt="" />
            </MDBCol>
            <MDBCol lg="11" md="12" sm="12" className="align-self-center">
              <h2
                style={{
                  "padding-bottom": "5%",
                  "padding-top": "2%"
                }}
              >
                Registriere dich als PAXET-Pakethub
              </h2>
            </MDBCol>
          </MDBRow>
          <MDBRow no-gutters>
            <MDBCol size="12" />
          </MDBRow>
        </MDBContainer>

        {/*	Hier wird der Cookie und deren Parameter bestimmt	*/}
        <CookieConsent
          location="bottom"
          buttonText=<a href="">Akzeptieren.</a>
          cookieName="CookieConsent"
          acceptOnScroll={true}
          acceptOnScrollPercentage={10}
          style={{ background: "#2B373B" }}
          buttonStyle={{ color: "#4e503b", fontSize: "13px" }}
          expires={150}
        >
          Diese Webseite benutzt Cookies, um die Benutzererfahrung zu
          verbessern. Indem Sie diese Website nutzen, erklären Sie sich mit
          dieser Verwendung einverstanden.
        </CookieConsent>
      </div>
    );
  }
}

const mapStateToProps = function(state) {
  return {
    home: state.home
  };
};
export default connect()(Pakethub);
